frappe.ui.form.on('Stock Entry', {
    onload: function (frm) {
        frm.fields_dict['custom_graded_by'].get_query = function() {
            return {
                filters: {
                    'custom_archived': 0
                }
            };
        };
    }
});

frappe.ui.form.on('Scan', {
    refresh(frm) {
        frm.add_custom_button("Scan QR Code", () => {

            const farm = cur_frm.doc.farm;
            const action = cur_frm.doc.action;

            if (!farm || !action) {
                frappe.throw("Please select a farm and action before scanning.");
                return;
            }

            if (!window.Html5Qrcode) {
                const script = document.createElement("script");
                script.src = "https://unpkg.com/html5-qrcode";
                script.onload = () => {
                    console.log("Html5Qrcode library loaded successfully.");
                    showQrScannerDialog();
                };
                script.onerror = () => {
                    console.error("Failed to load Html5Qrcode library.");
                    frappe.msgprint("Failed to load QR scanning library. Please check your network connection.");
                };
                document.head.appendChild(script);
            } else {
                showQrScannerDialog();
            }



            function showQrScannerDialog() {
                const dialog = new frappe.ui.Dialog({
                    title: "Scan QR Code",
                    fields: [
                        { fieldname: 'qr_scanner', fieldtype: 'HTML', options: '' }
                    ]
                });

                const $wrapper = dialog.fields_dict.qr_scanner.$wrapper;
                $wrapper.html('<div id="qr-reader" style="width: 100%;"></div>');

                dialog.show();

                setTimeout(() => {
                    const qrReaderElement = document.getElementById("qr-reader");
                    if (qrReaderElement) {
                        initializeQrScanner(qrReaderElement, dialog);
                    } else {
                        console.error("QR Reader element not found!");
                        frappe.msgprint("Error initializing QR scanner. Please try again.");
                    }
                }, 300);
            }

            let isProcessing = false;
            let createStock = false;
            let boxLabelData  = null;
            let isPaused = false;
            let farmPackListDocId = null;
            let boxStickerDocId = null;
            let stockEntryDoc;

            function initializeQrScanner(qrReaderElement, dialog) {
                const html5QrCode = new Html5Qrcode("qr-reader");

                html5QrCode.start(
                    { facingMode: "environment" }, 
                    {
                        fps: 15, 
                        qrbox: { width: 250, height: 250 } 
                    },
                    async (decodedText, decodedResult) => {
                        if (isProcessing || isPaused) {
                            return;
                        }
                        isProcessing = true;



                    },
                    (errorMessage) => {
                        console.warn("Scanning error:", errorMessage);
                    }
                ).catch((err) => {
                    console.error("Error starting QR scanner:", err);
                    frappe.msgprint("Unable to access the camera. Please ensure permissions are granted and try again.");
                });

                dialog.on("hide", function () {
                    html5QrCode.stop().then(() => {
                        console.log("QR Scanner stopped.");
                    }).catch((err) => {
                        console.error("Error stopping QR scanner:", err);
                    });
                });
            }

            function getStockEntryIdFromUrl(stockEntryUrl) {
                const stock_entry_url_arr = stockEntryUrl.split("/");
                const arrLength = stock_entry_url_arr.length;
                const stock_entry_id = stock_entry_url_arr[arrLength - 1];

                return stock_entry_id;
            }



        });
    }
});

frappe.ui.form.on('Stock Entry', {
    refresh: function(frm){
        $.each(frm.doc.items, function(i, row){
            if (row.uom == 'Stems') {
                row.custom_number_of_stems = row.qty;
            }
        });
    }
});

frappe.ui.form.on('Stock Entry', {
    custom_farm: function(frm) {
        if (frm.doc.stock_entry_type === "Grading") {
            let warehouseMapping = {
                "Burguret": { source: "Burguret Receiving Cold Store - TL", target: "Burguret Graded General - TL" },
                "Turaco": { source: "Turaco Receiving Cold Store - TL", target: "Turaco Graded General - TL" },
                "Pendekeza": { source: "Pendekeza Receiving Cold Store - TL", target: "Pendekeza Graded General - TL" }
            };

            let selectedFarm = frm.doc.custom_farm;

            if (selectedFarm && warehouseMapping[selectedFarm]) {
                frm.set_value("from_warehouse", warehouseMapping[selectedFarm].source);
                frm.set_value("to_warehouse", warehouseMapping[selectedFarm].target);
            }
        }
    },

    custom_bunched_by: function(frm) {
        if (frm.doc.stock_entry_type === "Grading") {
            if (frm.doc.custom_bunched_by) {
                let bunched_by_value = frm.doc.custom_bunched_by;

                if (frm.doc.items && frm.doc.items.length > 0) {
                    frm.doc.items.forEach((item, idx) => {
                        frappe.model.set_value(item.doctype, item.name, 'uom', bunched_by_value); 
                    });

                } else {
                    frappe.throw("No items found in the child table.");
                }

            }
        }
    },

    stock_entry_type: function(frm) {
        if (frm.doc.stock_entry_type === "Grading") {
            frm.trigger("custom_farm");
            frm.trigger("custom_bunched_by");
        }
    }
});

frappe.ui.form.on('Stock Entry', {
    custom_farm: function(frm) {
        if (frm.doc.stock_entry_type === "Field Rejects") {
            const greenhouse = frm.doc.custom_greenhouse;

            let warehouseMapping = {
                "Burguret": { source: `${greenhouse}`, target: "Burguret Field Rejects - TL" },
                "Turaco": { source: `${greenhouse}`, target: "Turaco Field Rejects - TL" },
                "Pendekeza": { source: `${greenhouse}`, target: "Pendekeza Field Rejects - TL" }
            };

            let selectedFarm = frm.doc.custom_farm;

            if (selectedFarm && warehouseMapping[selectedFarm]) {
                frm.set_value("from_warehouse", warehouseMapping[selectedFarm].source);
                frm.set_value("to_warehouse", warehouseMapping[selectedFarm].target);
            }
        }
    },

    stock_entry_type: function(frm) {
        if (frm.doc.stock_entry_type === "Field Rejects") {
            frm.trigger("custom_farm");
        }
    }
});

frappe.ui.form.on('Stock Entry', {
    after_save: function(frm) {
        if (frm.doc.docstatus === 0 && frm.doc.stock_entry_type === 'Grading') {

            const grading_details = {
