});

frappe.ui.form.on('Scan', {
	refresh(frm) {
	    toggle_scan_data_field(frm);
	},

	farm(frm) {
	    toggle_scan_data_field(frm);
	},

	action(frm) {
	    toggle_scan_data_field(frm);
	},



	async scan_data(frm) {


	    let scan_value = frm.doc.scan_data;
	    let farm = frm.doc.farm;
        let action = frm.doc.action;
        let boxStickerDocId = frm.doc.box_sticker || null;


	    if (scan_value && !frm.is_processing_stock_entry) {
	        frm.is_processing_stock_entry = true;
	        frm.set_df_property('scan_data', 'read_only', 1);

	        if (action === 'Packing') {
	            if (!frm.doc.opl_data) {

	                frm.set_value('opl_data', scan_value);
	                const oplId = scan_value.split('/').pop();
	                frm.set_value('order_pick_list', oplId)

	                frappe.msgprint('Order Pick List scanned. Now scan bunch labels.');
	                frm.set_df_property('scan_data', 'read_only', 0);
	                frm.set_value('scan_data', '');


	                frm.is_processing_stock_entry = false;
	            } else {

	                let bunchLabelData = scan_value;
	                try {
                        await process_bunch_scan(frm.doc.opl_data, bunchLabelData, farm, action, frm, boxStickerDocId);
                        frappe.msgprint('Bunch scanned. Scan next bunch or Close Box');
                        frm.set_df_property('scan_data', 'read_only', 0);
                        frm.set_value('scan_data', '');
                    } catch (error) {
                        frappe.msgprint(`Error processing bunch scan: ${error.message}`);

                    }

                    frm.set_df_property('scan_data', 'read_only', 0);
	                frm.set_value('scan_data', '');
	                frm.is_processing_stock_entry = false;
	            }


	        } else if (action === 'Grading') {
	            // Start with fetching the grader's name.
	            //If it is not a grader label and it's the first scan(grader field is not populated), throw error telling user to scan grader label first.
	            const data = JSON.parse(scan_value);
	            const bunchLabelData = data;

	            if (!frm.doc.grader) {
	               if (!data.grader ) {
    	               frappe.throw("Please scan the Grader label first!")
    	           }

    	            //Store the grader in the grader field
	                frm.set_value('grader', data.grader)
	                frappe.msgprint('Grader Label scanned. Now scan bunch labels.');
                    frm.set_df_property('scan_data', 'read_only', 0);
                    frm.set_value('scan_data', '');


                    frm.is_processing_stock_entry = false;

	            } else {


                    try {
                        await process_grading_bunch_scan(bunchLabelData, farm, action, frm);
                        frappe.msgprint(`Bunch scanned successfully. Scan next bunch belonging to ${frm.doc.graders_name}!`);
                        frm.set_df_property('scan_data', 'read_only', 0);
                        frm.set_value('scan_data', '');
                    } catch (error) {

                        frappe.msgprint(`Error processing bunch scan: ${error.message}`);

                    }

                    frm.set_df_property('scan_data', 'read_only', 0);
	                frm.set_value('scan_data', '');
	                frm.is_processing_stock_entry = false;
	            }



	        } else if (action === 'Receiving') {
                trigger_stock_entry(scan_value, farm, action).finally(() => {
                    frm.set_df_property('scan_data', 'read_only', 0);
                    frm.set_value('scan_data', '');
                    frm.is_processing_stock_entry = false;
                });
            } else if (action === 'Receiving Quarantined') {
                await trigger_stock_entry(scan_value, farm, action).finally(() => {
                    frm.set_df_property('scan_data', 'read_only', 0);
                    frm.set_value('scan_data', '');
                    frm.is_processing_stock_entry = false;
                });

            } else if (action === 'Id') {
                let stockEntryName;

                let variety, qty, breeder, grower, uom, harvester, greenhouse, block__bed_number, stockEntryDoc, items,
                    stem_length, graded_by, bunched_by;

                // This is grading qr code data
                if ( scan_value.includes("url=") && scan_value.includes("bunch_id=") ) {

                    const params = Object.fromEntries(
                        scan_value.split(';').map(pair => pair.trim().split('='))
                    );

                    const decodedUrl = params.url;
                    stockEntryName = getNameFromUrl(decodedUrl);

                    try {
                        stockEntryDoc = await frappe.db.get_doc('Stock Entry', stockEntryName);

                    } catch (error) {
                        frappe.throw(`Failed to fetch Stock Entry: ${error}`);
                    }

                    items = stockEntryDoc.items;

                    stem_length = stockEntryDoc.custom_stem_length;
                    graded_by = stockEntryDoc.custom_graded_by;

                    variety = items[0].item_code;
                    bunched_by = items[0].uom;
                    qty = items[0].qty;

                    frappe.msgprint(`Graded by: ${graded_by}, Variety: ${variety}, Bunched By: ${bunched_by}, Stem Length: ${stem_length}`)


                // This is harvesting qr code data
                } else if (scan_value.startsWith('http')) {
                    stockEntryName = getNameFromUrl(scan_value);

                    try {
                        stockEntryDoc = await frappe.db.get_doc('Stock Entry', stockEntryName);

                    } catch (error) {
                        frappe.throw(`Failed to fetch Stock Entry: ${error}`);
                    }

                    items = stockEntryDoc.items;

                    breeder = stockEntryDoc.custom_breeder;
                    grower = stockEntryDoc.custom_grower;
                    greenhouse = stockEntryDoc.custom_greenhouse;
                    harvester = stockEntryDoc.custom_harvester;
                    block__bed_number = stockEntryDoc.custom_block__bed_number;


                    items.forEach((item) => {
                        variety = item.item_code;
                        qty = item.qty;
                    });

                    frappe.msgprint(`Harvester: ${harvester}, Greenhouse: ${greenhouse}, Bay: ${block__bed_number}, Variety: ${variety}, No. of Stems: ${qty} `);
                }



                frm.set_df_property('scan_data', 'read_only', 0);
                frm.set_value('scan_data', '');
                frm.is_processing_stock_entry = false;

            } 
	    }
	}

});

async function process_grading_bunch_scan(bunchLabelData, farm, action, frm) {

    try {
        // Get the stock entry with the bunch Id
        // Check if bunch is scanned via the custom_scanned_grading field
        const existingEntry = await frappe.db.get_list('Stock Entry', {
            fields: ['name', 'custom_scanned_grading'],
            filters: {
                custom_bunch_id: bunchLabelData.bunch_id
            },
            limit: 1
        });



        // Check if bunch is already scanned
        if (existingEntry.length > 0 && existingEntry[0].custom_scanned_grading === 1) {
            frappe.throw('Bunch has already been scanned.');
        }

        // Create a new stock entry
        const stockEntry = frappe.model.get_new_doc('Stock Entry');

        stockEntry.stock_entry_type = 'Grading';

        stockEntry.from_warehouse = `${farm} Receiving Cold Store - TL`;
        stockEntry.to_warehouse = `${farm} Graded General - TL`;
        stockEntry.custom_farm = farm;
        stockEntry.custom_graded_by = frm.doc.grader;
        stockEntry.custom_stem_length = bunchLabelData.stem_length;
        stockEntry.custom_bunched_by = bunchLabelData.bunch_size;
        stockEntry.custom_bunch_id = bunchLabelData.bunch_id;
        stockEntry.custom_scanned_grading = 1;

        stockEntry.items = [{
            item_code: bunchLabelData.variety,
            uom: bunchLabelData.bunch_size,
            qty: 1
        }];

        await frappe.db.insert(stockEntry);
        frappe.msgprint('Stock transfered successfully.');
    } catch (error) {
        frappe.throw(__('Error creating Stock Entry: ') + error.message);
    }

}

let farmPackListDocId = null;



function getNameFromUrl(url) {
    const urlArray = url.split("/");
    const arrayLength = urlArray.length;
    const name = urlArray[arrayLength - 1];

    return name;
}



async function trigger_stock_entry(scan_value, farm, action) {
    await createStockEntry(scan_value, farm, action);
    if (action === 'Receiving') {
        frappe.msgprint(`Received Bucket and Created Stock Entry successfully`);
        frappe.msgprint(``)
    } else if (action === 'Receiving Quarantined') {
        frappe.msgprint(`Received and moved Bucket to quarantine`);
    }

}



async function process_bunch_scan(opl_url, bunchLabelData, farm, action, frm, boxStickerDocId) {

    const oplName = getNameFromUrl(opl_url);

    const bunchData = JSON.parse(bunchLabelData)

    const existingEntries = await frappe.db.get_list('Stock Entry', {
        fields: ['name', 'custom_bunch_id'],
        filters: {
            custom_bunch_id: bunchData.bunch_id
        },
        limit: 1
    });

    // If no matching stock entry is found
    if (existingEntries.length === 0) {
        frappe.throw(`No Stock Entry found for Bunch ID: ${bunchData.bunch_id}`);
    }
    const bunchStockEntryName = existingEntries[0].name;
    const stockEntryUrl = `/app/stock-entry/${bunchStockEntryName}`;
    let bunchName;



    // Incremented with successful scans
    let scannedItems = frm.doc.scanned_bunches || 0;
    let totalBunches = 0;



    let stockEntryDoc;
    try {
        stockEntryDoc = await frappe.db.get_doc('Stock Entry', bunchStockEntryName);
    } catch (error) {
        frappe.throw(`Failed to fetch Stock Entry: ${error}`);
    }

    if (stockEntryDoc.custom_scanned ) {
        frappe.throw("Bunch aready scanned!");
        return;
    }

    // frappe.call({
    //     method: "upande_tambuzi.server_scripts.update_custom_scanned.update_custom_scanned",
    //     args: { stock_entry_name: stockEntryName }
    // });

    // returns an object with total items and scanned items
    let varietyMatch = {};


    try {
        varietyMatch = await validateLabels(oplName, bunchLabelData, scannedItems);

    } catch (error) {
        frappe.throw(`Error validating labels: ${error}`);
        return;
    }

    totalBunches = varietyMatch.totalItems;
    scannedItems = varietyMatch.scannedItems;
    frm.set_value('scanned_bunches', scannedItems);

    bunchName = bunchData.variety;

    if (!varietyMatch.valid) {
        frappe.throw(`${bunchName} does not match the Order Pick List!`);
    } else {
        // Show the number of bunches scanned and the bunches remaining
        // Fetch from OPL and FPL
        frappe.msgprint(`${scannedItems} of ${totalBunches} Bunches scanned!`);
    }

    try {
        await createStockEntry(bunchStockEntryName, farm, action);
        frappe.msgprint(`Transfered stock successfully`);
    } catch (error) {
        frappe.throw(`Error creating stock entry: ${error}`);
    }



    if (!boxStickerDocId) {
        await createBoxSticker(oplName, bunchStockEntryName, frm);
    } else {
        await addVarietyToBoxSticker(boxStickerDocId, bunchStockEntryName);
    }

    await createOrGetFarmPackList(oplName, bunchStockEntryName, boxStickerDocId);






}



async function addVarietyToBoxSticker(boxStickerDocId, bunchStockEntryName) {
    try {
        const stockEntry = await frappe.db.get_doc("Stock Entry", bunchStockEntryName);

        const variety = stockEntry.items[0].item_code;
        const uom = stockEntry.items[0].uom;

        const response = frappe.call({
            method: 'upande_tambuzi.server_scripts.add_variety_to_box_sticker.add_variety_to_sticker',
            args: {
                box_label_sticker_name: boxStickerDocId,
                variety: variety,
                uom: uom
            },
            callback: function (response) {
                frappe.msgprint(`${variety} added to Box Label Sticker`);
            }
        });

    } catch (err) {
        frappe.throw(`Error adding ${variety} to Box Label Sticker: ${err}`);
    }
}



async function createBoxSticker(oplName, bunchStockEntryName, frm) {
    try {
        const orderId = oplName;

        // Box Id should be numerical and autoincremental. Can be fetched from the
        // name of the previous box sticker entry + 1

        // const lastBox = await frappe.db.get_value("Box Label", {"docstatus": ["!=", 2]}, "name", {order_by: "creation ASC"});

        const boxes = await frappe.db.get_list("Box Label", {
            filters: { "docstatus": ["!=", 2] },
            fields: ["name"],
            order_by: "creation DESC"
        });

        let newBoxNumber = 1;

        if (boxes.length > 0 && boxes[0].name) {
            const match = boxes[0].name.match(/BOX-(\d+)/);
            if (match) {
                newBoxNumber = parseInt(match[1]) + 1;
            }
        }

        const box_id = String(newBoxNumber).padStart(5, '0');
        const stockEntryId = bunchStockEntryName;

        // Fetch Sales Order & Stock Entry
        const orderPick = await frappe.db.get_doc("Order Pick List", orderId);

        const stockEntry = await frappe.db.get_doc("Stock Entry", stockEntryId);

        const itemCode = stockEntry.items[0].item_code;
        const uom = stockEntry.items[0].uom;
        const length = stockEntry.custom_stem_length;
        const customerId = orderPick.customer;
        const sales_order = orderPick.sales_order;

        const salesOrderDoc = await frappe.db.get_doc("Sales Order", sales_order);
        const customerPurchaseOrder = salesOrderDoc.po_no;

        const response = await frappe.call({
            method: 'upande_tambuzi.server_scripts.create_box_sticker.create_box_sticker',
            args: {
                variety: itemCode,
                uom: uom,
                customer: customerId,
                length: length,
                box_number: box_id,
                customer_purchase_order: customerPurchaseOrder
            },
            callback: function (response) {
                boxStickerDocId = response.message.docname
                frm.set_value('box_sticker', boxStickerDocId)
                // frappe.msgprint("Started Creating Box Label Sticker!")
            }
        })
    } catch (err) {
        frappe.throw(`Error creating Box Label: ${err}`)
    }

}



async function createOrGetFarmPackList(oplName, bunchStockEntryName, boxStickerDocId) {
    const { sales_order, farm } = await getSalesOrderAndFarmFromOPL(oplName);

    if (!sales_order || farm == "Unknown" ) {
        frappe.throw("Sales Order or Warehouse details missing in Order Pick List.");
        return;
    }

    const existingFPL = await frappe.db.get_list("Farm Pack List", {
        filters: {
            custom_sales_order: sales_order,
            custom_farm: farm
        },
        fields: ["name", "docstatus"]

    });

    const isSubmitted = existingFPL.some(fpl => fpl.docstatus === 1);

    if (isSubmitted) {
        frappe.throw("Farm Pack List already submitted. Check your Order Pick List and scan again")
    }

    if (existingFPL.length > 0) {
        farmPackListDocId = existingFPL[0].name;
        await addBunchToFarmPackList(farmPackListDocId, bunchStockEntryName, oplName, farm, boxStickerDocId);
        frappe.msgprint(`Bunch added to existing Farm Pack List: ${farmPackListDocId}`);
    } else {
        await createFarmPackListEntry(bunchStockEntryName, oplName, farm);
    }
}



async function createFarmPackListEntry(bunchStockEntryName, oplName, farm) {
    try {
        const orderId = oplName;
        const stockEntryId = bunchStockEntryName;

        // Fetch Sales Order & Stock Entry
        const orderPick = await frappe.db.get_doc("Order Pick List", orderId);

        const stockEntry = await frappe.db.get_doc("Stock Entry", stockEntryId);

        if (!orderPick || !stockEntry || !stockEntry.items.length) {
            frappe.throw("Invalid Sales Order or Stock Entry data");
        }

        const sourceWarehouse = `${farm} Dispatch Cold Store - TL`;

        const itemCode = stockEntry.items[0].item_code;
        const uom = stockEntry.items[0].uom;

        // Quantity scanned should always be one per scan
        const quantity = 1;
        const customerId = orderPick.customer;
        const sales_order_id = orderPick.sales_order;


        const stem_from_bunch = {
            "Bunch (5)": 5,
            "Bunch (6)": 6,
            "Bunch (10)": 10,
            "Bunch (12)": 12
        } 

        const bunch_stems = stem_from_bunch[uom];

        // Get box id from box label
        const boxLabelDoc = await frappe.db.get_doc('Box Label', boxStickerDocId);
        const boxId = boxLabelDoc.box_number;

        // Create new Farm Pack List document
        const packListDoc = frappe.model.get_new_doc("Farm Pack List");
        packListDoc.custom_sales_order = sales_order_id;
        packListDoc.custom_farm = farm;
        packListDoc.pack_list_item = [{
            item_code: itemCode,
            bunch_uom: uom,
            bunch_qty: quantity,
            source_warehouse: sourceWarehouse,
            sales_order_id: sales_order_id,
            customer_id: customerId,
            box_id: boxId,
            custom_number_of_stems: bunch_stems,
        }];

        // Save document
        const savedDoc = await frappe.db.insert(packListDoc);

        farmPackListDocId = savedDoc.name;

        frappe.msgprint("Farm Pack List entry created successfully!");
    } catch (error) {
        console.error("Error saving Farm Pack List:", error);
        frappe.throw(`Error saving Farm Pack List: ${error.message}`);
    }

} 



async function addBunchToFarmPackList(farmPackListDocId, bunchStockEntryName, oplName, farm, boxStickerDocId) {

    // Get box id from box label
    const boxLabelDoc = await frappe.db.get_doc('Box Label', boxStickerDocId);
    const boxId = boxLabelDoc.box_number;

    try {
        await frappe.call({
            method: 'upande_tambuzi.server_scripts.update_farm_pack_list.add_bunch_to_farm_pack_list',
            args: {
                farm_pack_list_doc_id: farmPackListDocId,
                bunch_SE_name: bunchStockEntryName,
                opl_name: oplName, 
                farm: farm,
                box_id: boxId
            },
            callback: function (response) {
