async function createStockEntry(scan_value, farm, action) {



    let variety;
    let qty;
    let breeder;
    let grower;
    let uom;
    let harvester;
    let greenhouse;
    let block__bed_number;
    let stockEntryDoc;

    // Use the stockEntryName to get the stock_entry_data field data needed for
    // stock transfer
    if (action == "Receiving") {
        try {
            const { item_code, bucket_id } = JSON.parse(scan_value);

            const stockEntries = await frappe.db.get_list('Stock Entry', {
               filters: {
                   'custom_bucket_id': bucket_id,
                   'docstatus': 1
               },
               fields: ['name', 'custom_scanned', 'custom_greenhouse', 'custom_harvester', 'custom_block__bed_number', 'items']
            });

            if (stockEntries.length === 0) {
                frappe.throw(`No Stock Entry found for bucket_id: ${bucket_id}`);
                return;
            }

            const stockEntryDoc = await frappe.db.get_doc('Stock Entry', stockEntries[0].name);

            if (stockEntryDoc.custom_scanned ) {
                frappe.throw("Bucket already scanned!");
                return;
            }

            frappe.call({
                method: "upande_tambuzi.server_scripts.update_custom_scanned.update_custom_scanned",
                args: { stock_entry_name: stockEntryDoc.name }
            });

            const items = stockEntryDoc.items;
            greenhouse = stockEntryDoc.custom_greenhouse;
            harvester = stockEntryDoc.custom_harvester_payroll_number;
            block__bed_number = stockEntryDoc.custom_block__bed_number;


            if (Array.isArray(items) && items.length > 0) {
                variety = items[0].item_code;
                qty = items[0].qty;
            }

            frappe.msgprint(`Harvester: ${harvester}, Greenhouse: ${greenhouse}, Bay: ${block__bed_number}, No. of Stems: ${qty} `)

        } catch (error) {
            frappe.throw(`Failed to fetch Stock Entry: ${error}`);
        }


    }

    if (action === "Packing") {
        const stockEntryName = scan_value;

        try {
            stockEntryDoc = await frappe.db.get_doc('Stock Entry', stockEntryName);

        } catch (error) {
            frappe.throw(`Failed to fetch Stock Entry: ${error}`);
        }

        const items = stockEntryDoc.items;

        items.forEach((item) => {
            variety = item.item_code;

            // Flowers are packed per 1 bunch
            qty = 1;
            uom = item.uom;
        });
    }

    if (action === 'Receiving Quarantined') {
        try {
            const { item_code, bucket_id } = JSON.parse(scan_value);

            const stockEntries = await frappe.db.get_list('Stock Entry', {
               filters: {
                   'custom_bucket_id': bucket_id,
                   'docstatus': 1
               },
               fields: ['name', 'custom_scanned', 'custom_greenhouse', 'custom_harvester', 'custom_block__bed_number', 'items']
            });

            if (stockEntries.length === 0) {
                frappe.throw(`No Stock Entry found for bucket_id: ${bucket_id}`);
                return;
            }

            const stockEntryDoc = await frappe.db.get_doc('Stock Entry', stockEntries[0].name);

            if (stockEntryDoc.custom_scanned ) {
                frappe.throw("Bucket already scanned!");
                return;
            }

            const items = stockEntryDoc.items;

            greenhouse = stockEntryDoc.custom_greenhouse;
            harvester = stockEntryDoc.custom_harvester;
            block__bed_number = stockEntryDoc.custom_block__bed_number;

            if (Array.isArray(items) && items.length > 0) {
                variety = items[0].item_code;
                qty = items[0].qty;
            }

            frappe.msgprint(`Harvester: ${harvester}, Greenhouse: ${greenhouse}, Bay: ${block__bed_number}, No. of Stems: ${qty} `)


            frappe.call({
                method: "upande_tambuzi.server_scripts.update_custom_scanned.update_custom_scanned",
                args: { stock_entry_name: stockEntryDoc.name }
            });

        } catch (error) {
            frappe.throw(`Failed to fetch Stock Entry: ${error}`);
        }


    }

    const stock_entry_type_mapping = {
        "Receiving": "Receiving",
        "Packing": "Packing",
        "Receiving Quarantined": "Receiving Quarantined",
    };

    const stock_entry_type = stock_entry_type_mapping[action];

    // Change the mapping of the location to check the location doctype 
    const locationMapping = {
        "Burguret Receiving": { source: `${greenhouse}`, target: "Burguret Receiving Cold Store - TL" },
        "Burguret Receiving Quarantined": { source: `${greenhouse}`, target: "Burguret Quarantine Store - TL" },
        "Burguret Packing": { source: "Burguret Graded Sold - TL", target: "Burguret Dispatch Cold Store - TL" },

        "Turaco Receiving": { source: `${greenhouse}`, target: "Turaco Receiving Cold Store - TL" },
        "Turaco Receiving Quarantined": { source: `${greenhouse}`, target: "Turaco Quarantine Store - TL" },
        "Turaco Packing": { source: "Turaco Graded Sold - TL", target: "Turaco Dispatch Cold Store - TL" },

        "Pendekeza Receiving": { source: `${greenhouse}`, target: "Pendekeza Receiving Cold Store - TL" },
        "Pendekeza Receiving Quarantined": { source: `${greenhouse}`, target: "Pendekeza Quarantine Store - TL" },
        "Pendekeza Packing": { source: "Pendekeza Graded Sold - TL", target: "Pendekeza Dispatch Cold Store - TL" }
    };

    const scanLocation = `${farm} ${action}`;

    const stockmvt = locationMapping[scanLocation];

    if (!stockmvt) {
        frappe.msgprint(`Invalid scan location: ${scanLocation}`);
        return;
    }
    stock_entry_data = {
        "location data": stockmvt,
        "variety": variety,
        "quantity": qty,
        "breeder": breeder,
        "grower": grower,
        "harvester": harvester,
        "greenhouse": greenhouse,
        "stock entry type": stock_entry_type,
        "uom": uom,
        "block__bed_number": block__bed_number
    };



    const created_stock_entry = frappe.call({
        method: "upande_tambuzi.server_scripts.create_stock_entry.create_stock_entry",
        args: {
            stock_entry_data: JSON.stringify(stock_entry_data),
        }
    });





    return created_stock_entry;

}

