                }
            ).catch((err) => {
                console.error("Error starting QR scanner:", err);
                frappe.msgprint("Unable to access the camera. Please ensure permissions are granted and try again.");
            });

            dialog.on("hide", function () {
                html5QrCode.stop().then(() => {
                    console.log("QR Scanner stopped.");
                }).catch((err) => {
                    console.error("Error stopping QR scanner:", err);
                });
            });
        }

        // Automatically start scanning when farm and block are filled
        frm.fields_dict.farm.df.onchange = checkAndStartScanner;
        frm.fields_dict.block.df.onchange = checkAndStartScanner;
        frm.fields_dict.blck.df.onchange = checkAndStartScanner;
