async function validateLabels(oplName, bunchLabelData, scannedItems, frm) {
    const { sales_order, farm } = await getSalesOrderAndFarmFromOPL(oplName);
    let orderPickVariety;
    let stockEntryUOM;
    let stockEntryVariety;

    // Total bunches should be fetched from the OPL row for the variety
    let totalBunches = 0;
    let scannedBunches = 0;
    let box_number = 0;
    let box_label = "";

    const bunchData = JSON.parse(bunchLabelData);

    try {
        stockEntryVariety = bunchData.variety;
        stockEntryUOM = bunchData.bunch_size;
    } catch (error) {
        frappe.throw(`Error fetching bunch data: ${error}`);
    }

    const existingFPL = await frappe.db.get_list("Farm Pack List", {
        filters: {
            custom_sales_order: sales_order,
            custom_farm: farm
        },
        fields: ["name", "docstatus"]
    });

    const isSubmitted = existingFPL.some(fpl => fpl.docstatus === 1);

    if (isSubmitted) {
        frappe.throw("Farm Pack List already submitted. Check your Order Pick List and scan again");
    }

    try {
        const orderPickList = await frappe.db.get_doc("Order Pick List", oplName);

        if (!orderPickList || !orderPickList.locations || orderPickList.locations.length === 0) {
            frappe.msgprint(`No valid Order Pick List found for ${oplName}`);
            return { totalItems: 0, scannedItems: 0, valid: false };
        }

        totalBunches = orderPickList.locations.reduce((sum, row) => {
            return sum + (row.qty || 0);
        }, 0);

        frm.set_value('total_bunches', totalBunches);

        // Find matching rows in OPL for the scanned variety and UOM
        const matchingOPLRows = orderPickList.locations.filter(row => 
            row.item_code === stockEntryVariety && row.uom === stockEntryUOM
        );

        if (matchingOPLRows.length === 0) {
            frappe.throw(`No matching variety ${stockEntryVariety} with UOM ${stockEntryUOM} found in Order Pick List`);
            return { totalItems: 0, scannedItems: 0, valid: false };
        }

        // Initialize box_id with null to ensure we explicitly set it
        let box_id = null;

        if (!existingFPL.length) {
            // If no existing FPL, use the first matching OPL row's box_id
            box_id = matchingOPLRows[0].custom_box_id;
            box_label = matchingOPLRows[0].custom_box_label
        } else {
            // Get the FPL document
            const fplDoc = await frappe.db.get_doc("Farm Pack List", existingFPL[0].name);
            const fplTable = fplDoc.pack_list_item || [];

            // Track scanned bunches for each box of this variety+UOM
            const boxScannedCounts = {};

            // Count bunches already scanned for each box
            fplTable.forEach(fplRow => {
                if (fplRow.item_code === stockEntryVariety && fplRow.bunch_uom === stockEntryUOM) {
                    const boxKey = fplRow.box_id.toString();
                    boxScannedCounts[boxKey] = (boxScannedCounts[boxKey] || 0) + fplRow.bunch_qty;
                }
            });

            // Find the first box that isn't full
            for (const oplRow of matchingOPLRows) {
                const boxKey = oplRow.custom_box_id.toString();
                const scannedForBox = boxScannedCounts[boxKey] || 0;

                // If this box has fewer scanned bunches than required
                if (scannedForBox < oplRow.qty) {
                    box_id = oplRow.custom_box_id;
                    box_label = matchingOPLRows[0].custom_box_label
                    break;
                }
            }

            // If all boxes are full or no box was found
            if (box_id === null && matchingOPLRows.length > 0) {
                frappe.msgprint(`All boxes for ${stockEntryVariety} with UOM ${stockEntryUOM} are full.`);
                // Use the last box as fallback
                box_id = matchingOPLRows[matchingOPLRows.length - 1].custom_box_id;
            }

            // Calculate total scanned bunches
            fplTable.forEach(row => {
                scannedBunches += row.bunch_qty;
            });

            // Check if all required bunches for this variety are already scanned
            const requiredBunchesForVariety = matchingOPLRows.reduce((sum, row) => sum + (row.qty || 0), 0);
            const scannedBunchesForVariety = fplTable
                .filter(row => row.item_code === stockEntryVariety && row.bunch_uom === stockEntryUOM)
                .reduce((sum, row) => sum + (row.bunch_qty || 0), 0);

            if (scannedBunchesForVariety >= requiredBunchesForVariety) {
                frappe.msgprint(`Excess ${stockEntryVariety} scanned for this order`);
                return { totalItems: totalBunches, scannedItems: scannedBunches, valid: false };
            }
        }

        // Verify the scanned variety matches an expected variety in OPL
        for (let i = 0; i < orderPickList.locations.length; i++) {
            let currVariety = orderPickList.locations[i].item_code;
            let currUOM = orderPickList.locations[i].uom;

            if (currVariety === stockEntryVariety) {
                orderPickVariety = currVariety;

                if (currUOM !== stockEntryUOM) {
                    frappe.throw(`Scanned wrong bunch size ${currVariety}: Expected ${currUOM}, but scanned ${stockEntryUOM}`);
                }
            }
        }

        // Make sure box_id is always set
        if (box_id === null) {
            frappe.throw(`Could not determine appropriate box ID for ${stockEntryVariety}`);
            return { totalItems: totalBunches, scannedItems: scannedBunches, valid: false };
        }

        // Set box_number to the determined box_id
        box_number = box_id;

    } catch (error) {
        frappe.throw(`Error processing data: ${error}`);
    }

    return {
        totalItems: totalBunches,
        scannedItems: scannedBunches,
        box_number: box_number,
        box_label: box_label,
        valid: orderPickVariety === stockEntryVariety
    };
}



