frappe.ui.form.on('Scan', {
    refresh(frm) {
        frm.add_custom_button("Scan QR Code", () => {

            const farm = cur_frm.doc.farm;
            const action = cur_frm.doc.action;

            if (!farm || !action) {
                frappe.throw("Please select a farm and action before scanning.");
                return;
            }

            if (!window.Html5Qrcode) {
                const script = document.createElement("script");
                script.src = "https://unpkg.com/html5-qrcode";
                script.onload = () => {
                    console.log("Html5Qrcode library loaded successfully.");
                    showQrScannerDialog();
                };
                script.onerror = () => {
                    console.error("Failed to load Html5Qrcode library.");
                    frappe.msgprint("Failed to load QR scanning library. Please check your network connection.");
                };
                document.head.appendChild(script);
            } else {
                showQrScannerDialog();
            }



            function showQrScannerDialog() {
                const dialog = new frappe.ui.Dialog({
                    title: "Scan QR Code",
                    fields: [
                        { fieldname: 'qr_scanner', fieldtype: 'HTML', options: '' }
                    ]
                });

                const $wrapper = dialog.fields_dict.qr_scanner.$wrapper;
                $wrapper.html('<div id="qr-reader" style="width: 100%;"></div>');

                dialog.show();

                setTimeout(() => {
                    const qrReaderElement = document.getElementById("qr-reader");
                    if (qrReaderElement) {
                        initializeQrScanner(qrReaderElement, dialog);
                    } else {
                        console.error("QR Reader element not found!");
                        frappe.msgprint("Error initializing QR scanner. Please try again.");
                    }
                }, 300);
            }

            let isProcessing = false;
            let createStock = false;
            let boxLabelData  = null;
            let isPaused = false;
            let farmPackListDocId = null;
            let boxStickerDocId = null;
            let stockEntryDoc;

            function initializeQrScanner(qrReaderElement, dialog) {
                const html5QrCode = new Html5Qrcode("qr-reader");

                html5QrCode.start(
                    { facingMode: "environment" }, 
                    {
                        fps: 15, 
                        qrbox: { width: 250, height: 250 } 
                    },
                    async (decodedText, decodedResult) => {
                        if (isProcessing || isPaused) {
                            return;
                        }
                        isProcessing = true;

                        try {
                            if (action === "Packing") {
                                if (!boxLabelData) {
                                    boxLabelData = decodedText;


                                    frappe.msgprint(`Box label scanned. Please scan the bunch label. ${boxLabelData}`);

                                    isPaused = true;

                                    frappe.confirm("Ready to scan the bunch label?", () => {
                                        isPaused = false;
                                    });

                                    isProcessing = false;
                                    return;
                                } else {
                                    const bunchLabelData = decodedText;

                                    const urlArray = decodedText.split("/");
                                    const arrayLength = urlArray.length;
                                    const stockEntryName = urlArray[arrayLength - 1];

                                    try {
                                        stockEntryDoc = await frappe.db.get_doc('Stock Entry', stockEntryName);
                                    } catch (error) {
                                        frappe.throw(`Failed to fetch Stock Entry: ${error}`);
                                    }

                                    if (stockEntryDoc.custom_scanned ) {
                                        frappe.throw("Bunch aready scanned!");
                                        return;
                                    }

                                    const varietyMatch = await validateLabels(boxLabelData, bunchLabelData);

                                    if (!varietyMatch) {
                                        frappe.throw("Bunch label does not match the box label!");
                                    } else {
                                        frappe.msgprint("Bunch and Box labels match!")


                                        await createOrGetFarmPackList(boxLabelData, bunchLabelData)


                                        if (!boxStickerDocId) {
                                            await createBoxSticker(boxLabelData, bunchLabelData)
                                        } else {
                                            await addVarietyToBoxSticker(boxStickerDocId, bunchLabelData);
                                        }

                                        try {
                                            await createStockEntry(bunchLabelData, farm, action);
                                            frappe.msgprint(`Transfered stock successfully`);
                                        } catch (error) {
                                            frappe.throw(`Error creating stock entry: ${error}`)
                                        }

                                        isPaused = true;

                                        frappe.confirm("Bunch Scanned! Ready to scan the next bunch label? Press No to CLOSE box", () => {
                                            isPaused = false;
                                        });
                                    }
                                    // isPaused = true;
                                }
                            }

                            if (action == "Receiving" ) {


                                createStock = true;
                            }

                            if (action == "Grading") {
                                const urlArray = decodedText.split("/");
                                stockEntryName = urlArray[urlArray.length - 1];
                                stockEntryDoc = await frappe.db.get_doc('Stock Entry', stockEntryName);

                                await frappe.call({
                                    method: "frappe.client.submit",
                                    args: {
                                        doc: stockEntryDoc,
                                    }
                                });
                                frappe.msgprint(`Stock Entry ${stockEntryName} submitted successfully`);
                                html5QrCode.stop();
                                dialog.hide();
                            }

                            if (createStock) {
                                const doc_name = await createStockEntry(decodedText, farm, action);
                                frappe.msgprint(`Created Stock Entry successfully`);
                                html5QrCode.stop();
                                dialog.hide();
                            }

                        } catch (error) {
                            frappe.throw(`Error while creating stock entry: ${error}`)
                        } finally {
                            isProcessing = false;
                        }



                    },
                    (errorMessage) => {
                        console.warn("Scanning error:", errorMessage);
                    }
                ).catch((err) => {
                    console.error("Error starting QR scanner:", err);
                    frappe.msgprint("Unable to access the camera. Please ensure permissions are granted and try again.");
                });

                dialog.on("hide", function () {
                    html5QrCode.stop().then(() => {
                        console.log("QR Scanner stopped.");
                    }).catch((err) => {
                        console.error("Error stopping QR scanner:", err);
                    });
                });
            }

            function getStockEntryIdFromUrl(stockEntryUrl) {
                const stock_entry_url_arr = stockEntryUrl.split("/");
                const arrLength = stock_entry_url_arr.length;
                const stock_entry_id = stock_entry_url_arr[arrLength - 1];

                return stock_entry_id;
            }

            async function createOrGetFarmPackList(boxLabelData, bunchLabelData) {
                const { sales_order, farm } = await getSalesOrderAndFarmFromOPL(boxLabelData);

                if (!sales_order || farm == "Unknown" ) {
                    frappe.throw("Sales Order or Warehouse details missing in Order Pick List.");
                    return;
                }

                const existingFPL = await frappe.db.get_list("Farm Pack List", {
                    filters: {
                        custom_sales_order: sales_order,
                        custom_farm: farm
                    },
                    fields: ["name"]

                })

                if (existingFPL.length > 0) {
                    farmPackListDocId = existingFPL[0].name;
                    await addBunchToFarmPackList(farmPackListDocId, bunchLabelData, boxLabelData, farm);
                    frappe.msgprint(`Bunch added to existing Farm Pack List: ${farmPackListDocId}`);
                } else {
                    await createFarmPackListEntry(bunchLabelData, boxLabelData, farm);
                }

                return;

            }

            async function getSalesOrderAndFarmFromOPL(boxLabelData) {
                const parsedBoxData = JSON.parse(boxLabelData);
                const orderPickListId = parsedBoxData.order_id;

                const oplDoc = await frappe.db.get_doc('Order Pick List', orderPickListId);

                if (!oplDoc) {
                    frappe.throw("Order Pick List not found for the given box label.");
                }

                // Can also be fetched from the direct field; "source_warehouse"
                const farmName = oplDoc.source_warehouse.split(" ")[0]
                                || oplDoc.locations[0].warehouse?.split(" ")[0]
                                || oplDoc.locations[0].custom_source_warehouse?.split(" ")[0]
                                || "Unknown";

                return {
                    sales_order: oplDoc.sales_order,
                    farm: farmName
                };
            }

            async function createBoxSticker(boxLabelData, bunchLabelData) {
                try {
                    const parsedBoxData = JSON.parse(boxLabelData);
                    const orderId = parsedBoxData.order_id;
                    const box_id = parsedBoxData.box_id;
                    const stockEntryId = getStockEntryIdFromUrl(bunchLabelData);

                    // Fetch Sales Order & Stock Entry
                    const orderPick = await frappe.db.get_doc("Order Pick List", orderId);

                    const stockEntry = await frappe.db.get_doc("Stock Entry", stockEntryId);

                    const itemCode = stockEntry.items[0].item_code;
                    const uom = stockEntry.items[0].uom;
                    const length = stockEntry.custom_stem_length;
                    const customerId = orderPick.customer;

                    const response = await frappe.call({
                        method: 'upande_tambuzi.server_scripts.create_box_sticker.create_box_sticker',
                        args: {
                            variety: itemCode,
                            uom: uom,
                            customer: customerId,
                            length: length,
                            box_number: box_id,
                        },
                        callback: function (response) {
                            boxStickerDocId = response.message.docname
                            frappe.msgprint("Box Label Sticker Created Successfully!")
                        }
                    })



                } catch (err) {
                    frappe.throw(`Error creating Box Label: ${err}`)
                }
            }


            async function addVarietyToBoxSticker(boxStickerDocId, bunchLabelData) {
                try {
                    const stockEntryId = getStockEntryIdFromUrl(bunchLabelData);
                    const stockEntry = await frappe.db.get_doc("Stock Entry", stockEntryId);

                    const variety = stockEntry.items[0].item_code
                    const uom = stockEntry.items[0].uom

                    const response = frappe.call({
                        method: 'upande_tambuzi.server_scripts.add_variety_to_box_sticker.add_variety_to_sticker',
                        args: {
                            box_label_sticker_name: boxStickerDocId,
                            variety: variety,
                            uom: uom
                        },
                        callback: function (response) {
                            frappe.msgprint(`${variety} added to Box Label Sticker`)
                        }
                    })

                } catch (err) {
                    frappe.throw(`Error adding ${variety} to Box Label Sticker: ${err}`)
                }
            }


