                        source_warehouse: sourceWarehouse,
                        sales_order_id: sales_order_id,
                        customer_id: customerId,
                        box_id: boxId
                    }];

                    // Save document
                    const savedDoc = await frappe.db.insert(packListDoc);

                    farmPackListDocId = savedDoc.name;

