            async function addBunchToFarmPackList(farmPackListDocId, bunchLabelData, boxLabelData, farm) {
                try {
                    await frappe.call({
                        method: 'upande_tambuzi.server_scripts.update_farm_pack_list.add_bunch_to_farm_pack_list',
                        args: {
                            farm_pack_list_doc_id: farmPackListDocId,
                            bunch_label_data: bunchLabelData,
                            box_label_data: boxLabelData, 
                            farm: farm,
                        },
                        callback: function (response) {
                            frappe.msgprint(`Added bunch to Farm Pack List: ${farmPackListDocId}`);
                        }
                    });
                } catch (error) {
                    console.error('Error adding bunch to Farm Pack List:', error);
                    frappe.throw(`Error adding bunch to Farm Pack List: ${error}`);
                }
            }


            async function validateLabels(boxLabelData, bunchLabelData) {
                const stock_entry_url_arr = bunchLabelData.split("/");
                const arrLength = stock_entry_url_arr.length;
                const stock_entry_id = stock_entry_url_arr[arrLength - 1];

                const parsedBoxData = JSON.parse(boxLabelData);
                const orderId = parsedBoxData.order_id;

                let orderPickVariety;
                let stockEntryVariety;

                // Get the variety associated with the stock_entry_id (bunch) first
                try {
                    const stockEntryDocument = await frappe.db.get_doc('Stock Entry', stock_entry_id);

                    if (stockEntryDocument) {
                        stockEntryVariety = stockEntryDocument.items[0].item_code;
                    } else {
                        frappe.msgprint(`No Stock Entry found for ${stock_entry_id}`);
                    }
                } catch (error) {
                    frappe.throw(`Error fetching Stock Entry: ${error}`);
                }

                // Get the variety associated with the order_pick_list and stockEntryVariety
                try {
                    const orderPickList = await frappe.db.get_doc("Order Pick List", orderId);
                    const numberOfItems = orderPickList.locations.length;

                    if (orderPickList) {

                        for (let i = 0; i < numberOfItems; i++) {
                            let currVariety = orderPickList.locations[i].item_code;

                            if (currVariety === stockEntryVariety ) {
                                orderPickVariety = currVariety;
                            }
                        }

                    } else {
                        frappe.msgprint(`No Order Pick List found for ${orderId}`);
                    }
                } catch (error) {
                    frappe.throw(`Error fetching Order Pick List: ${error}`);
                }



                if (orderPickVariety !== stockEntryVariety) {
                    return false;
                }

                return true;
            } 

            async function createStockEntry(decodedText, farm, action) {
                const urlArray = decodedText.split("/");
                const arrayLength = urlArray.length;
                const stockEntryName = urlArray[arrayLength - 1];


                let variety;
                let qty;
                let breeder;
                let grower;
                let uom;
                let harvester;
                let greenhouse;
                let block__bed_number;

                // Use the stockEntryName to get the stock_entry_data field data needed for
                // stock transfer
                if (action == "Receiving") {
                    try {
                        stockEntryDoc = await frappe.db.get_doc('Stock Entry', stockEntryName);
                    } catch (error) {
                        frappe.throw(`Failed to fetch Stock Entry: ${error}`);
                    }

                    if (stockEntryDoc.custom_scanned ) {
                        frappe.throw("Bucket aready scanned!");
                        return;
                    }

                    const items = stockEntryDoc.items;

                    breeder = stockEntryDoc.custom_breeder;
                    grower = stockEntryDoc.custom_grower;
                    greenhouse = stockEntryDoc.custom_greenhouse;
                    harvester = stockEntryDoc.custom_harvester;
                    block__bed_number = stockEntryDoc.custom_block__bed_number


                    items.forEach((item) => {
                        variety = item.item_code;
                        qty = item.qty;
                    });
                }

                if (action === "Packing") {
                    try {
                        stockEntryDoc = await frappe.db.get_doc('Stock Entry', stockEntryName);
                    } catch (error) {
                        frappe.throw(`Failed to fetch Stock Entry: ${error}`);
                    }



                    const items = stockEntryDoc.items;

                    items.forEach((item) => {
                        variety = item.item_code;

                        // Flowers are packed per 1 bunch
                        qty = 1;
                        uom = item.uom;
                    });
                }

                const stock_entry_type_mapping = {
                    "Receiving": "Receiving",
                    "Grading": "",
                    "Packing": "Packing",
                    "Loading to Truck": "Material Transfer"
                };

                const stock_entry_type = stock_entry_type_mapping[action];

                // Maps the location of scan to the movement of stock
                const locationMapping = {
                    "Burguret Receiving": { source: `${greenhouse}`, target: "Burguret Receiving Cold Store - TL" },
                    // "Burguret Grading": { source: "Burguret Ungraded Store - TL", target: "Burguret Graded Store - TL" },
                    "Burguret Packing": { source: "Burguret Graded Sold - TL", target: "Burguret Dispatch Cold Store - TL" },
                    // "Burguret Loading to Truck": { source: "Burguret - Packaging", target: "Burguret - Packaging" },
                    "Turaco Receiving": { source: `${greenhouse}`, target: "Turaco Receiving Cold Store - TL" },
                    // "Turaco Grading": { source: "Turaco Ungraded Store - TL", target: "Turaco Graded Store - TL" },
                    "Turaco Packing": { source: "Turaco Graded Sold - TL", target: "Turaco Dispatch Cold Store - TL" },
                    // "Turaco Loading to Truck": { source: "Turaco - Packaging", target: "Turaco - Packaging" },
                    "Pendekeza Receiving": { source: `${greenhouse}`, target: "Pendekeza Receiving Cold Store - TL" },
                    // "Pendekeza Grading": { source: "Pendekeza Ungraded Store - TL", target: "Pendekeza Graded Store - TL" },
                    "Pendekeza Packing": { source: "Pendekeza Graded Sold - TL", target: "Pendekeza Dispatch Cold Store - TL" }
                    // "Pendekeza Loading to Truck": { source: "Pendekeza - Packaging", target: "Pendekeza - Packaging" }
                };

                const scanLocation = `${farm} ${action}`;

                const stockmvt = locationMapping[scanLocation];

                if (!stockmvt) {
                    frappe.msgprint(`Invalid scan location: ${scanLocation}`);
                    return;
                }
                stock_entry_data = {
                    "location data": stockmvt,
                    "variety": variety,
                    "quantity": qty,
                    "breeder": breeder,
                    "grower": grower,
                    "harvester": harvester,
                    "greenhouse": greenhouse,
                    "stock entry type": stock_entry_type,
                    "uom": uom,
                    "block__bed_number": block__bed_number
                };

                const created_stock_entry = frappe.call({
                    method: "upande_tambuzi.server_scripts.create_stock_entry.create_stock_entry",
                    args: {
                        stock_entry_data: JSON.stringify(stock_entry_data),
                    }
                });

                await frappe.call({
                    method: "upande_tambuzi.server_scripts.update_custom_scanned.update_custom_scanned",
                    args: { stock_entry_name: stockEntryName }
                });


                return created_stock_entry;

            }

        });
    }
});

frappe.ui.form.on('Stock Entry', {
    refresh: function(frm){
        $.each(frm.doc.items, function(i, row){
            if (row.uom == 'Stems') {
                row.custom_number_of_stems = row.qty;
            }
        });
    }
});

frappe.ui.form.on('Stock Entry', {
    custom_farm: function(frm) {
        if (frm.doc.stock_entry_type === "Grading") {
            let warehouseMapping = {
                "Burguret": { source: "Burguret Receiving Cold Store - TL", target: "Burguret Graded General - TL" },
                "Turaco": { source: "Turaco Receiving Cold Store - TL", target: "Turaco Graded General - TL" },
                "Pendekeza": { source: "Pendekeza Receiving Cold Store - TL", target: "Pendekeza Graded General - TL" }
            };

            let selectedFarm = frm.doc.custom_farm;

            if (selectedFarm && warehouseMapping[selectedFarm]) {
                frm.set_value("from_warehouse", warehouseMapping[selectedFarm].source);
                frm.set_value("to_warehouse", warehouseMapping[selectedFarm].target);
            }
        }
    },

    stock_entry_type: function(frm) {
        if (frm.doc.stock_entry_type === "Grading") {
            frm.trigger("custom_farm");
        }
    }
});

frappe.ui.form.on('Stock Entry', {
    custom_farm: function(frm) {
        if (frm.doc.stock_entry_type === "Field Rejects") {
            const greenhouse = frm.doc.custom_greenhouse;

            let warehouseMapping = {
                "Burguret": { source: `${greenhouse}`, target: "Burguret Field Rejects - TL" },
                "Turaco": { source: `${greenhouse}`, target: "Turaco Field Rejects - TL" },
                "Pendekeza": { source: `${greenhouse}`, target: "Pendekeza Field Rejects - TL" }
            };

            let selectedFarm = frm.doc.custom_farm;

            if (selectedFarm && warehouseMapping[selectedFarm]) {
                frm.set_value("from_warehouse", warehouseMapping[selectedFarm].source);
                frm.set_value("to_warehouse", warehouseMapping[selectedFarm].target);
            }
        }
    },

    stock_entry_type: function(frm) {
        if (frm.doc.stock_entry_type === "Field Rejects") {
            frm.trigger("custom_farm");
        }
    }
});

frappe.ui.form.on('Stock Entry', {
    before_save: function(frm) {
        if (frm.doc.stock_entry_type === 'Grading') {

            const grading_details = {
                "name": frm.doc.name,
                "variety": frm.doc.items[0].item_code,
                "grader": frm.doc.custom_graded_by
            };

            const response = frappe.call({
                method: "upande_tambuzi.server_scripts.qr_code_generator.generate_qr_code",
                args: {
                    stock_entry_details: JSON.stringify(grading_details)
                },
                callback: function (response) {
                    frm.doc.custom_bunch_qr_code = response.message;
                }
            });



        }
    },

});
