script:
frappe.ui.form.on('Scan', {
    refresh(frm) {
        frm.add_custom_button("Scan QR Code", () => {

            const scanLocation = cur_frm.doc.scan_location;

            if (!scanLocation) {
                frappe.throw("Scan location is missing. Please select it before scanning.");
                return;
            }

            if (!window.Html5Qrcode) {
                const script = document.createElement("script");
                script.src = "https://unpkg.com/html5-qrcode";
                script.onload = () => {
                    console.log("Html5Qrcode library loaded successfully.");
                    showQrScannerDialog();
                };
                script.onerror = () => {
                    console.error("Failed to load Html5Qrcode library.");
                    frappe.msgprint("Failed to load QR scanning library. Please check your network connection.");
                };
                document.head.appendChild(script);
            } else {
                showQrScannerDialog();
            }



            function showQrScannerDialog() {
                const dialog = new frappe.ui.Dialog({
                    title: "Scan QR Code",
                    fields: [
                        { fieldname: 'qr_scanner', fieldtype: 'HTML', options: '' }
                    ]
                });

                const $wrapper = dialog.fields_dict.qr_scanner.$wrapper;
                $wrapper.html('<div id="qr-reader" style="width: 100%;"></div>');

                dialog.show();

                setTimeout(() => {
                    const qrReaderElement = document.getElementById("qr-reader");
                    if (qrReaderElement) {
                        initializeQrScanner(qrReaderElement, dialog);
                    } else {
                        console.error("QR Reader element not found!");
                        frappe.msgprint("Error initializing QR scanner. Please try again.");
                    }
                }, 300);
            }






            function initializeQrScanner(qrReaderElement, dialog) {
                const html5QrCode = new Html5Qrcode("qr-reader");

                html5QrCode.start(
                    { facingMode: "environment" }, 
                    {
                        fps: 10, 
                        qrbox: { width: 250, height: 250 } 
                    },
                    (decodedText, decodedResult) => {
                        createStockEntry(decodedText, scanLocation);
                        frappe.msgprint(`${decodedText}`);
                        html5QrCode.stop();
                        dialog.hide();


                    },
                    (errorMessage) => {
                        console.warn("Scanning error:", errorMessage);
                    }
                ).catch((err) => {
                    console.error("Error starting QR scanner:", err);
                    frappe.msgprint("Unable to access the camera. Please ensure permissions are granted and try again.");
                });

                dialog.on("hide", function () {
                    html5QrCode.stop().then(() => {
                        console.log("QR Scanner stopped.");
                    }).catch((err) => {
                        console.error("Error stopping QR scanner:", err);
                    });
                });
            }

            function createStockEntry(decodedText, scanLocation) {
                const details = {};
                decodedText.split("\n").forEach((line) => {
                    const [key, value] = line.split(":").map((part) => part.trim());
                    if (key && value) details[key] = value;
                });

                const source_warehouse = details["Source Warehouse"];
                const variety = details.Variety;
                const qty = parseInt(details["Number of Stems"], 10);
                const breeder = details.Breeder;
                const grower = details.Grower;
                const stock_entry_type = source_warehouse ? "Material Transfer" : "Material Receipt";


                const locationMapping = {
                    "Burguret Receiving Area": { source: null, target: "Burguret Received Store - TL" },
                    "Burguret Grading Area": { source: "Burguret Received Store - TL", target: "Burguret General Store - TL" },
                    // "Burguret Packaging Area": { source: "Burguret - Grading", target: "Burguret - Packaging" },
                    "Turaco Receiving Area": { source: null, target: "Turaco Received Store - TL" },
                    "Turaco Grading Area": { source: "Turaco Received Store - TL", target: "Turaco General Store - TL" },
                    // "Turaco Packaging Area": { source: "Turaco - Grading", target: "Turaco - Packaging" },
                    "Pendekeza Receiving Area": { source: null, target: "Pendekeza Received Store - TL" },
                    "Pendekeza Grading Area": { source: "Pendekeza Received Store - TL", target: "Pendekeza General Store - TL" },
                    // "Pendekeza Packaging Area": { source: "Pendekeza - Grading", target: "Pendekeza - Packaging" }
                };

                const locationData = locationMapping[scanLocation];

                if (!locationData) {
                    frappe.msgprint(`Invalid scan location: ${scanLocation}`);
                    return;
                }
                stock_entry_data = {
                    "location data": locationData,
                    "source warehouse": source_warehouse,
                    "variety": variety,
                    "quantity": qty,
                    "breeder": breeder,
                    "grower": grower,
                    "stock entry type": stock_entry_type
                };



                const created_stock_entry = frappe.call({
                    method: "upande_tambuzi.server_scripts.create_stock_entry.create_stock_entry",
                    args: {
                        stock_entry_data: JSON.stringify(stock_entry_data),
                    },
                });
            }

        });
    }
});
