frappe.ui.form.on('Scan', {
    refresh(frm) {
        frm.add_custom_button("Scan QR Code", () => {

            const farm = cur_frm.doc.farm;
            const action = cur_frm.doc.action;

            if (!farm || !action) {
                frappe.throw("Please select a farm and action before scanning.");
                return;
            }

            if (!window.Html5Qrcode) {
                const script = document.createElement("script");
                script.src = "https://unpkg.com/html5-qrcode";
                script.onload = () => {
                    console.log("Html5Qrcode library loaded successfully.");
                    showQrScannerDialog();
                };
                script.onerror = () => {
                    console.error("Failed to load Html5Qrcode library.");
                    frappe.msgprint("Failed to load QR scanning library. Please check your network connection.");
                };
                document.head.appendChild(script);
            } else {
                showQrScannerDialog();
            }



            function showQrScannerDialog() {
                const dialog = new frappe.ui.Dialog({
                    title: "Scan QR Code",
                    fields: [
                        { fieldname: 'qr_scanner', fieldtype: 'HTML', options: '' }
                    ]
                });

                const $wrapper = dialog.fields_dict.qr_scanner.$wrapper;
                $wrapper.html('<div id="qr-reader" style="width: 100%;"></div>');

                dialog.show();

                setTimeout(() => {
                    const qrReaderElement = document.getElementById("qr-reader");
                    if (qrReaderElement) {
                        initializeQrScanner(qrReaderElement, dialog);
                    } else {
                        console.error("QR Reader element not found!");
                        frappe.msgprint("Error initializing QR scanner. Please try again.");
                    }
                }, 300);
            }

            let isProcessing = false;
            let createStock = false;
            let boxLabelData  = null;
            let isPaused = false;
            let farmPackListDocId = null;
            let stockEntryDoc;

            function initializeQrScanner(qrReaderElement, dialog) {
                const html5QrCode = new Html5Qrcode("qr-reader");

                html5QrCode.start(
                    { facingMode: "environment" }, 
                    {
                        fps: 15, 
                        qrbox: { width: 250, height: 250 } 
                    },
                    async (decodedText, decodedResult) => {
                        if (isProcessing || isPaused) {
                            return;
                        }
                        isProcessing = true;

                        try {
                            if (action === "Packing") {
                                if (!boxLabelData) {
                                    boxLabelData = decodedText;
                                    frappe.msgprint(`Box label scanned. Please scan the bunch label. ${boxLabelData}`);

                                    isPaused = true;

                                    frappe.confirm("Ready to scan the bunch label?", () => {
                                        isPaused = false;
                                    });

                                    isProcessing = false;
                                    return;
                                } else {
                                    const bunchLabelData = decodedText;
                                    const varietyMatch = await validateLabels(boxLabelData, bunchLabelData);

                                    if (!varietyMatch) {
                                        frappe.throw("Bunch label does not match the box label!");
                                    } else {
                                        frappe.msgprint("Bunch and Box labels match!")

                                        if (!farmPackListDocId) {
                                            await createFarmPackListEntry(bunchLabelData, boxLabelData, farm);
                                        } else {
                                            await addBunchToFarmPackList(farmPackListDocId, bunchLabelData, boxLabelData, farm);
                                        }


                                        await createStockEntry(bunchLabelData, farm, action);
                                        frappe.msgprint(`Transfered stock successfully`);

                                        isPaused = true;

                                        frappe.confirm("Bunch Scanned! Ready to scan the next bunch label?", () => {
                                            isPaused = false;
                                        });
                                    }
                                    // isPaused = true;
                                }
                            }

                            if (action == "Receiving" ) {
                                createStock = true;
                            }

                            if (action == "Grading") {
                                const urlArray = decodedText.split("/");
                                stockEntryName = urlArray[urlArray.length - 1];
                                stockEntryDoc = await frappe.db.get_doc('Stock Entry', stockEntryName);

                                await frappe.call({
                                    method: "frappe.client.submit",
                                    args: {
                                        doc: stockEntryDoc,
                                    }
                                });
                                frappe.msgprint(`Stock Entry ${stockEntryName} submitted successfully`);
                                html5QrCode.stop();
                                dialog.hide();
                            }

                            if (createStock) {
                                const doc_name = await createStockEntry(decodedText, farm, action);
                                frappe.msgprint(`Created Stock Entry successfully`);
                                html5QrCode.stop();
                                dialog.hide();
                            }

                        } catch (error) {
                            frappe.throw(`Error while creating stock entry: ${error}`)
                        } finally {
                            isProcessing = false;
                        }



                    },
                    (errorMessage) => {
                        console.warn("Scanning error:", errorMessage);
                    }
                ).catch((err) => {
                    console.error("Error starting QR scanner:", err);
                    frappe.msgprint("Unable to access the camera. Please ensure permissions are granted and try again.");
                });

                dialog.on("hide", function () {
                    html5QrCode.stop().then(() => {
                        console.log("QR Scanner stopped.");
                    }).catch((err) => {
                        console.error("Error stopping QR scanner:", err);
                    });
                });
            }

            function getStockEntryIdFromUrl(stockEntryUrl) {
                const stock_entry_url_arr = stockEntryUrl.split("/");
                const arrLength = stock_entry_url_arr.length;
                const stock_entry_id = stock_entry_url_arr[arrLength - 1];

                return stock_entry_id;
            }

            async function createFarmPackListEntry(bunchLabelData, boxLabelData, farm) {
                try {
                    const response = await frappe.call({
                        method: 'upande_tambuzi.server_scripts.create_farm_pack_list_entry.create_farm_pack_list_entry',
                        args: {
                            bunch_label_data: bunchLabelData,
                            box_label_data: boxLabelData,
                            farm: farm,
                        },
                        callback: function (response) {
                            farmPackListDocId = response.message.docname;
                            frappe.msgprint(`Farm Pack List created: ${farmPackListDocId}`);
                        }
                    });
                } catch (error) {
                    console.error('Error saving Farm Pack List:', error);
                    frappe.throw(`Error saving Farm Pack List: ${error}`);
                }
            }

            async function addBunchToFarmPackList(farmPackListDocId, bunchLabelData, boxLabelData, farm) {
                try {
                    await frappe.call({
                        method: 'upande_tambuzi.server_scripts.update_farm_pack_list.add_bunch_to_farm_pack_list',
                        args: {
                            farm_pack_list_doc_id: farmPackListDocId,
                            bunch_label_data: bunchLabelData,
                            box_label_data: boxLabelData, 
                            farm: farm,
                        },
                        callback: function (response) {
                            frappe.msgprint(`Added bunch to Farm Pack List: ${farmPackListDocId}`);
                        }
                    });
                } catch (error) {
                    console.error('Error adding bunch to Farm Pack List:', error);
                    frappe.throw(`Error adding bunch to Farm Pack List: ${error}`);
                }
            }


            async function validateLabels(boxLabelData, bunchLabelData) {
                const stock_entry_url_arr = bunchLabelData.split("/");
                const arrLength = stock_entry_url_arr.length;
                const stock_entry_id = stock_entry_url_arr[arrLength - 1];

                const parsedBoxData = JSON.parse(boxLabelData);
                const orderId = parsedBoxData.order_id;

                let salesOrderVariety;
                let stockEntryVariety;

                // Get the variety associated with the order_id from Sales Order Doctype
                try {
                    const salesOrder = await frappe.db.get_doc("Sales Order", orderId);

                    if (salesOrder) {
                        // Should check for all the varieties in the sales order before validating
                        salesOrderVariety = salesOrder.items[0].item_code;
                    } else {
                        frappe.msgprint(`No Sales Order found for ${orderId}`);
                    }
                } catch (error) {
                    frappe.throw(`Error fetching Sales Order: ${error}`);
                }

                // Get the variety associated with the stock_entry_id
                try {
                    const stockEntryDocument = await frappe.db.get_doc('Stock Entry', stock_entry_id);

                    if (stockEntryDocument) {
                        stockEntryVariety = stockEntryDocument.items[0].item_code;
                    } else {
                        frappe.msgprint(`No Stock Entry found for ${stock_entry_id}`);
                    }
                } catch (error) {
                    frappe.throw(`Error fetching Stock Entry: ${error}`);
                }

                if (salesOrderVariety !== stockEntryVariety) {
                    return false;
                }

                return true;
            } 

            async function createStockEntry(decodedText, farm, action) {
                const urlArray = decodedText.split("/");
                const arrayLength = urlArray.length;
                const stockEntryName = urlArray[arrayLength - 1];


                let variety;
                let qty;
                let breeder;
                let grower;
                let uom;
                let harvester;
                let greenhouse;
                let block__bed_number;

                // Use the stockEntryName to get the stock_entry_data field data needed for
                // stock transfer
                if (action == "Receiving") {
                    try {
                        stockEntryDoc = await frappe.db.get_doc('Stock Entry', stockEntryName);
                    } catch (error) {
                        frappe.throw(`Failed to fetch Stock Entry: ${error}`);
                    }

                    const items = stockEntryDoc.items;

                    breeder = stockEntryDoc.custom_breeder;
                    grower = stockEntryDoc.custom_grower;
                    greenhouse = stockEntryDoc.custom_greenhouse;
                    harvester = stockEntryDoc.custom_harvester;
                    block__bed_number = stockEntryDoc.custom_block__bed_number


                    items.forEach((item) => {
                        variety = item.item_code;
                        qty = item.qty;
                    });
                }

                if (action === "Packing") {
                    try {
                        stockEntryDoc = await frappe.db.get_doc('Stock Entry', stockEntryName);
                    } catch (error) {
                        frappe.throw(`Failed to fetch Stock Entry: ${error}`);
                    }

                    const items = stockEntryDoc.items;

                    items.forEach((item) => {
                        variety = item.item_code;
                        qty = item.qty;
                        uom = item.uom;
                    });
                }

                const stock_entry_type_mapping = {
                    "Receiving": "Receiving",
                    "Grading": "",
                    "Packing": "Packing",
                    "Loading to Truck": "Material Transfer"
                };

                const stock_entry_type = stock_entry_type_mapping[action];

                // Maps the location of scan to the movement of stock
                const locationMapping = {
                    "Burguret Receiving": { source: `${greenhouse}`, target: "Burguret Receiving Cold Store - TL" },
                    // "Burguret Grading": { source: "Burguret Ungraded Store - TL", target: "Burguret Graded Store - TL" },
                    "Burguret Packing": { source: "Burguret Sold Store - TL", target: "Burguret Dispatch Cold Store - TL" },
                    // "Burguret Loading to Truck": { source: "Burguret - Packaging", target: "Burguret - Packaging" },
                    "Turaco Receiving": { source: `${greenhouse}`, target: "Turaco Receiving Cold Store - TL" },
                    // "Turaco Grading": { source: "Turaco Ungraded Store - TL", target: "Turaco Graded Store - TL" },
                    "Turaco Packing": { source: "Turaco Sold Store - TL", target: "Turaco Dispatch Cold Store - TL" },
                    // "Turaco Loading to Truck": { source: "Turaco - Packaging", target: "Turaco - Packaging" },
                    "Pendekeza Receiving": { source: `${greenhouse}`, target: "Pendekeza Receiving Cold Store - TL" },
                    // "Pendekeza Grading": { source: "Pendekeza Ungraded Store - TL", target: "Pendekeza Graded Store - TL" },
                    "Pendekeza Packing": { source: "Pendekeza Sold Store - TL", target: "Pendekeza Dispatch Cold Store - TL" }
                    // "Pendekeza Loading to Truck": { source: "Pendekeza - Packaging", target: "Pendekeza - Packaging" }
                };

                // scanLocation depends on the 'farm' and 'action' fields
                const scanLocation = `${farm} ${action}`;

                const stockmvt = locationMapping[scanLocation];

                if (!stockmvt) {
                    frappe.msgprint(`Invalid scan location: ${scanLocation}`);
                    return;
                }
                stock_entry_data = {
                    "location data": stockmvt,
                    "variety": variety,
                    "quantity": qty,
                    "breeder": breeder,
                    "grower": grower,
                    "harvester": harvester,
                    "greenhouse": greenhouse,
                    "stock entry type": stock_entry_type,
                    "uom": uom,
                    "block__bed_number": block__bed_number
                };

                const created_stock_entry = frappe.call({
                    method: "upande_tambuzi.server_scripts.create_stock_entry.create_stock_entry",
                    args: {
                        stock_entry_data: JSON.stringify(stock_entry_data),
                    }
                });



                return created_stock_entry;

            }

        });
    }
});

frappe.ui.form.on('Stock Entry', {
    refresh: function(frm){
        $.each(frm.doc.items, function(i, row){
            if (row.uom == 'Stems') {
                row.custom_number_of_stems = row.qty;
            }
        });
    }
});
