css:
.label-container {
    width: 140mm;
    height: auto; 
    line-height: 1;
    border: 4px solid black;
    font-family: Arial, sans-serif;
    text-transform: uppercase;
    font-weight: bold;
}

.product-row {
    display: flex;
    flex-direction: row;
    position: relative;
    justify-content: space-between;
}

.customer-label {
    font-size: 45px;
    font-weight: 900;
    line-height: 1;
}

.customer-p-o {
    font-size: 30px;
}

.label-section .label {
    font-size: 26px;
}

.label-header {
    text-align: center;
    font-size: 18px;
    font-weight: bold;
}

.label-value {
    padding-right: 5px;
    font-size: 20px;
}

.label {
    margin: 0;
    border: 0;
}

.label-section {
    display: flex;
    justify-content: space-between;
    border-top: 2px solid black;
    padding: 5px 0;
    font-size: 14px;
}

.label-section span {
    font-weight: bold;
}

.product-list {
    font-size: 20px;
    font-weight: bold;
    padding-right: 5px;

}
